<?php

use ApiPlatform\Core\Bridge\Symfony\Bundle\Test\ApiTestCase;
use App\Entity\Tag;
use Hautelook\AliceBundle\PhpUnit\RefreshDatabaseTrait;

class TagTest extends ApiTestCase
{
    use RefreshDatabaseTrait;

    const TOKEN = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6IkhzcmVVUFpIMGVJdm1pZGVTYkhJRmoyLXZBVkYyV3RwRFpNVTNOSnVhMVkifQ..YCPzx0o7PawIwnsb3J3YsirsPc-km1X_5YX7Cq3-t9o';

    public function testGetCollection(): void
    {
        // The client implements Symfony HttpClient's `HttpClientInterface`, and the response `ResponseInterface`
        $response = static::createClient([], ['headers' => ['authorization' => 'Bearer '.self::TOKEN]])->request('GET', 'api/tags');

        $this->assertResponseIsSuccessful();
        // Asserts that the returned content type is JSON-LD (the default)
        $this->assertResponseHeaderSame('content-type', 'application/ld+json; charset=utf-8');

        // Asserts that the returned JSON is a superset of this one
        $this->assertJsonContains([
            '@context' => '/api/contexts/Tag',
            '@id' => '/api/tags',
            '@type' => 'hydra:Collection',
            'hydra:totalItems' => 1,
        ]);

        // Because test fixtures are automatically loaded between each test, you can assert on them
        $this->assertCount(1, $response->toArray()['hydra:member']);

        // Asserts that the returned JSON is validated by the JSON Schema generated for this resource by API Platform
        // This generated JSON Schema is also used in the OpenAPI spec!
        $this->assertMatchesResourceCollectionJsonSchema(Tag::class);
    }

    public function testCreateTag(): void
    {
        $response = static::createClient([], ['headers' => ['authorization' => 'Bearer '.self::TOKEN]])->request('POST', 'api/tags', ['json' => [
            'name' => 'DVD',
        ]]);
        $this->assertResponseStatusCodeSame(201);
        $this->assertResponseHeaderSame('content-type', 'application/ld+json; charset=utf-8');
        $this->assertJsonContains([
            '@context' => '/api/contexts/Tag',
            '@type' => 'Tag',
            'name' => 'DVD',
        ]);
        $this->assertMatchesRegularExpression('~^\/api\/tags\/{\w*|\d*-*}*$~', $response->toArray()['@id']);
        $this->assertMatchesResourceItemJsonSchema(Tag::class);
    }
}
